(23, 'zxczxczxvcxz', '<pre style=\"box-sizing: border-box; margin: 0px; overflow: auto; padding: 10px; border: 1px solid rgb(204, 204, 204); border-radius: 0px; background: rgb(245, 245, 245); font-size: 14px; line-height: 1.4; color: rgb(37, 37, 37); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">&lt;?php    // Allowed extentions.    $allowedExts = array(&quot;gif&quot;, &quot;jpeg&quot;, &quot;jpg&quot;, &quot;png&quot;);     // Get filename.    $temp = explode(&quot;.&quot;, $_FILES[&quot;file&quot;][&quot;name&quot;]);     // Get extension.    $extension = end($temp);     // An image check is being done in the editor but it is best to    // check that again on the server side.    // Do not use $_FILES[&quot;file&quot;][&quot;type&quot;] as it can be easily forged.    $finfo = finfo_open(FILEINFO_MIME_TYPE);    $mime = finfo_file($finfo, $_FILES[&quot;file&quot;][&quot;tmp_name&quot;]);     if ((($mime == &quot;image/gif&quot;)    || ($mime == &quot;image/jpeg&quot;)    || ($mime == &quot;image/pjpeg&quot;)    || ($mime == &quot;image/x-png&quot;)    || ($mime == &quot;image/png&quot;))    &amp;&amp; in_array($extension, $allowedExts)) {        // Generate new random name.        $name = sha1(microtime()) . &quot;.&quot; . $extension;         // Save file in the uploads folder.        move_uploaded_file($_FILES[&quot;file&quot;][&quot;tmp_name&quot;], getcwd() . &quot;/uploads/&quot; . $name);         // Generate response.        $response = new StdClass;        $response-&gt;link = &quot;/uploads/&quot; . $name;        echo stripslashes(json_encode($response));    } ?&gt;</pre><p><br></p><p><img src=\"data:image/jpeg;base64,', '0', 58, 'zxczxczxvcxz');
